#!/usr/bin/env node
'use strict';

const commander = require('commander');

require('../lib/resource/isp').buildRemoveISPCommand(commander);

commander
    .usage('\n\n  $ ask remove [options] [command]')
    .description('The api command provides a number of sub-commands that enable you to remove skill resource(s) from your local skill project.')
    .parse(process.argv);

if (!process.argv.slice(2).length) {
    commander.outputHelp();
} else {
    const ADD_COMMAND_LIST = [
        'isp'
    ];
    if (ADD_COMMAND_LIST.indexOf(process.argv[2]) === -1) {
        console.log('Command not recognized. Please run "ask remove --help" for help.');
    }
}
