'use strict';

module.exports = {
    getDomainFromAlexaUsage: getDomainFromAlexaUsage,
    getRegionFromAlexaUsage: getRegionFromAlexaUsage
};

function getDomainFromAlexaUsage(lambdaResourceItem) {
    return lambdaResourceItem.alexaUsage[0].split('/')[0];
}

function getRegionFromAlexaUsage(lambdaResourceItem) {
    return lambdaResourceItem.alexaUsage[0].split('/')[1];
}
